<style lang="scss" scoped>
#home {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: auto;
  width: min(800px, 95%);
  height: min(550px, 95%);
  background-color: #F2F2F2;
  border-radius: 1rem;
  box-shadow: 1px 3px 5px hsla(0, 0%, 0%, 0.4), 1px 0px 10px hsla(282, 46%, 12%, 0.2);
  transform-origin: center center;
  will-change: transform;
  animation: anim-home 1s both;

  @keyframes anim-home {
    from {
      opacity: 0;
      transform: scale(0.8) translateY(-40%);
    }

    to {
      opacity: 1;
      transform: scale(1) translateY(0%);
    }
  }

  /* lightning svg element */
  .lightning {
    position: absolute;
    inset: 0;
    margin: auto;
    width: 100%;
    height: 100%;

    svg {
      width: 100%;
      height: 100%;

    }
  }

  /* -------------------- */


  .home-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 2.5rem;
    z-index: 2;

    h1 {
      font-size: 2.5rem;
    }

    p {
      margin-bottom: 1rem;
      font-weight: 400;
    }
  }

  .btns {
    display: flex;
    align-items: center;
    gap: 3rem;

    .btn-box {
      position: relative;
      border-radius: 1rem;

      button {
        width: 7rem;
        border: none;
        padding: 0.7rem 0rem;
        border-radius: 1rem;
        color: #fff;
        transition: background-color 0.3s ease, transform 0.2s ease;

        &:active {
          transition: transform 0.1s ease;
          transform: translateY(0.2rem);
        }

        &.training {
          background-color: #5E5392;

          &:hover {
            background-color: #6e62a7;
          }
        }

        &.play {
          background-color: #4999B0;
          position: relative;

          &:hover {
            background-color: #59acc3;
          }
        }
      }

      .btn-layer {
        position: absolute;
        width: 100%;
        height: 100%;
        inset: 0;
        border-radius: 1rem;
        z-index: -1;
        transform: translateY(0.2rem);
        box-shadow: 0 0 5px hsla(0, 0%, 0%, 0.5);
      }

      &.training-box .btn-layer {
        background-color: #39325a;
      }

      &.play-box .btn-layer {
        background-color: #34728b;
      }
    }

  }

  .results {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    z-index: 3;

    .heading {
      font-size: 2.5rem;
      margin-bottom: 1.5rem;
      font-weight: 500;
    }

    .stats {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 1.5rem;
      background-color: #C2DCE1;
      padding: 2rem 1.5rem;
      width: 20rem;
      border-radius: 1rem;
      margin-bottom: 2rem;

      .stats-block {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 1rem;

        h4 {
          font-weight: 400;
        }

        .stats-points {
          display: inline-block;
          font-size: 1.1rem;
          font-weight: 600;
        }

        .svg-box {
          width: 2.2rem;
          height: 2.2rem;

          svg {
            width: 100%;
            height: 100%;
          }
        }
      }
    }
  }

  .fade-enter-active,
  .fade-leave-active {
    transition: opacity 0.6s ease;
  }

  .fade-enter-from,
  .fade-leave-to {
    opacity: 0;
  }

  .fade-enter-to,
  .fade-leave-from {
    opacity: 1;
  }
}
</style>
